import React, { useState } from 'react';
import { Link as RouterLink } from 'react-router-dom'
import validator from 'validator'
import "./signup.scss";
import { regexPassword } from "../../utils";
import {
    Paper,
    Container,
    Link,
    Stack,
    Button,
    Box,
    Divider,
    Avatar,
    Typography,
    TextField,
    FilledInput,
    InputAdornment,
    IconButton,
    InputLabel,
    FormControl,
    FormHelperText,
} from "@mui/material";
import {
    Face as FaceIcon,
    Visibility,
    VisibilityOff,
} from "@material-ui/icons";

function Signup() {
    const [values, setValues] = useState({
        email: '',
        password: '',
        repeatPassword: '',
        showPassword: false,
        showRepeatPassword: false,
    })
    const [errors, setErrors] = useState({
        email: false,
        password: false,
        repeatPassword: false,
        fetchError: false,
        fetchErrorMsg: '',
    })

    const handleChange = (fieldName) => (Event) => {
        const currValue = Event.target.value
        switch (fieldName) {
            case 'email':
                validator.isEmail(currValue)
                    ? setErrors({ ...errors, email: false })
                    : setErrors({ ...errors, email: true})
                break
            case 'password':
                regexPassword.test(currValue)
                    ? setErrors({ ...errors, password: false})
                    : setErrors({ ...errors, password: true})
                break
            case 'repeatPassword':
                currValue === values.password
                    ? setErrors({ ...errors, repeatPassword: false})
                    : setErrors({ ...errors, repeatPassword: true})
                break
        }
        setValues({ ...values, [fieldName]: Event.target.value })
    }

    const handleShowPassword = (showPasswordField) => {
        setValues({
            ...values,
            [showPasswordField]: !values[showPasswordField],
        })
    }

    const handleSubmit = async (Event) => {
        Event.preventDefault()

        try {
            const res = await fetch('/api/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    email: values.email,
                    password: values.password,
                }),
            })

            if (!res.ok) {
                const error =await res.json()
                return setErrors({
                    ...errors,
                    fetchError: true,
                    fetchErrorMsg: error.msg,
                })
            }

            const data = await res.json()

            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg: data.msg,
            })

            setValues({
                email: '',
                password: '',
                repeatPassword: '',
                showPassword: false,
                showRepeatPassword: false,
            })
            return
        } catch (error) {
            setErrors({
                ...errors,
                fetchError: true,
                fetchErrorMsg:
                    'There was a problem with our server, please try again later'
            })
        }
    }

    return (
        <div className="signup">
            <Container sx={{ marginTop: 'calc(100vh - 45%' }} maxWidth='sm'>
                <Paper elevation={6}>
                    <Container
                        maxWidth='sm'
                        sx={{
                            display: 'flex',
                            flexDirection: 'column',
                            justifyContent: 'center',
                            alignItems: 'center',
                            paddingTop: '20px',
                        }}>
                        <Avatar
                            sx={{
                                display: 'flex',
                                flexDirection: 'column',
                                justifyContent: 'center',
                                alignItems: 'center',
                                paddingTop: '20px',
                            }}>
                            <FaceIcon
                                sx={{
                                    fontSize: 70,
                                }}
                            />
                        </Avatar>
                        <h2>Register a new account</h2>
                    </Container>
                    <Stack
                        component='form'
                        onSubmit={handleSubmit}
                        noValidate
                        spacing={6}
                        sx={{ bgcolor: '#f5f5f6', padding:'40px' }}>
                        <TextField
                            variant='filled'
                            type='email'
                            label='Email'
                            value={values.email}
                            onChange={handleChange('email')}
                            error={errors.email}
                            helperText={errors.email && 'Please insert a valid email address'}
                        />

                        <FormControl variant='filled'>
                            <InputLabel htmlFor='password-field'>Password</InputLabel>
                            <FilledInput
                                id='password-field'
                                type={values.showPassword ? 'text' : 'password'}
                                value={values.password}
                                onChange={handleChange('password')}
                                error={errors.password}
                                endAdornment={
                                    <InputAdornment position='end'>
                                        <IconButton
                                            aria-label='toggle password visibility'
                                            onClick={() => handleShowPassword('showPassword')}
                                            edge='end'>
                                            {values.showPassword ? <VisibilityOff /> : <Visibility />}
                                        </IconButton>
                                    </InputAdornment>
                                }
                            />

                            <FormHelperText error={errors.password}>
                                Password must be at least 8 characters, have one symbol, 1 uppercase letter, 1 lowercase letter, and 1 digit
                            </FormHelperText>
                        </FormControl>

                        <FormControl variant='filled'>
                            <InputLabel htmlFor='password-repeat-field'>Repeat Password</InputLabel>
                            <FilledInput
                                id='password-repeat-field'
                                type={values.showRepeatPassword ? 'text' : 'password'}
                                value={values.repeatPassword}
                                onChange={handleChange('repeatPassword')}
                                endAdornment={
                                    <InputAdornment position='end'>
                                        <IconButton
                                            aria-label='toggle password visibility'
                                            onClick={() => handleShowPassword('showRepeatPassword')}
                                            edge='end'>
                                            {values.showRepeatPassword ? (
                                                <VisibilityOff/>
                                            ) : (
                                                <Visibility/>
                                            )}
                                        </IconButton>
                                    </InputAdornment>
                                }
                            />
                            {errors.repeatPassword && (
                                <FormHelperText error={errors.repeatPassword}>
                                    Password must be the same as above
                                </FormHelperText>
                            )}
                        </FormControl>
                        <Box
                            sx={{
                                display: 'flex',
                                justifyContent: 'center',
                            }}>
                            <Button
                                variant='contained'
                                size='large'
                                type='submit'
                                sx={{
                                    minWidth: '70%',
                                }}>
                                Sign me up!
                            </Button>
                        </Box>
                        {errors.fetchError && (
                            <FormHelperText error>{errors.fetchErrorMsg}</FormHelperText>
                        )}
                        <Divider />
                        <Typography paragraph align='center'>
                            Already have an account?{' '}
                            <Link component={RouterLink} to='/login'>
                                Login here
                            </Link>
                        </Typography>
                    </Stack>
                </Paper>
            </Container>
        </div>
    );
}

export default Signup;
